import timeit

chars = ""

def naive(substr_len):
    for i in range(len(chars) - substr_len):
        if len(set(chars[i:i+substr_len])) == substr_len:
            break
    return i + substr_len

def get_marker(substr_len):
    char_set = set()
    start_idx = 0

    for i in range(len(chars)):
        char = chars[i]

        if char in char_set:
            while chars[start_idx] != char:
                char_set.remove(chars[start_idx])
                start_idx += 1
            start_idx += 1
            continue
        
        char_set.add(char)

        if len(char_set) == substr_len:
            break

    return i + 1

print(f"Part one: {get_marker(4)}")
print(f"Part two: {get_marker(14)}")

print(f"Part one: {naive(4)}")
print(f"Part two: {naive(14)}")

print(timeit.timeit("naive(14)", number=1000, globals=locals()))
print(timeit.timeit("get_marker(14)", number=1000, globals=locals()))